mapper.select:
SELECT menu.*, role_menu_rela.role_id
FROM a_menu menu
LEFT JOIN a_role_menu_rela role_menu_rela
ON role_menu_rela.menu_id = menu.id
AND role_menu_rela.role_id = #{roleId, jdbcType=BIGINT}
WHERE menu.apps_id = #{appsId, jdbcType=BIGINT}

SELECT menu.* FROM a_role_user_rela role_user_rela, a_role_menu_rela role_menu_rela, a_menu menu
WHERE role_user_rela.role_id = role_menu_rela.role_id
AND role_menu_rela.menu_id = menu.id
AND menu.apps_id = #{appsId, jdbcType=BIGINT}

  AND menu.type = #{type, jdbcType=SMALLINT}

AND role_user_rela.user_id = #{userId, jdbcType=BIGINT}
ORDER BY menu.order_id ASC, menu.id ASC
